    #{item}

select dmcs.* from diagnosis_mainsymp_concsymp dmcs INNER JOIN diagnosis_disease_rate ddr ON dmcs.disease_code = ddr.disease_code AND dmcs.main_symp_code = ddr.main_symp_code where dmcs.main_symp_code=#{mainSympCode} and dmcs.conc_symp_code IN
#{item}
AND IFNULL(dmcs.operate_type, '') not in ('D','H') AND IFNULL(ddr.operate_type, '') not in ('D','H')

select *  from diagnosis_mainsymp_concsymp dmcs
where dmcs.conc_symp_code IN

	#{item}

SELECT dmc.* from diagnosis_concomitant_symptom dcs INNER JOIN diagnosis_mainsymp_concsymp dmc ON dcs.symp_code = dmc.conc_symp_code INNER JOIN diagnosis_disease_rate ddr ON dmc.disease_code = ddr.disease_code AND dmc.main_symp_code = ddr.main_symp_code WHERE dmc.main_symp_code = #{mainSympCode} AND dcs.symp_name in
#{item}
AND ifnull(ddr.operate_type,'') NOT in ('D','H') AND ifnull(dmc.operate_type,'') NOT in ('D', 'H') AND ifnull(dcs.operate_type,'') NOT in ('D', 'H') AND dmc.rate > 0

SELECT dms.symp_code, dms.symp_name, dmc.disease_code, dcs.symp_name AS 'answer_title', dmc.conc_symp_code AS 'answer_code', dmc.rate AS 'answer_weight', dmq.question_code, dmq.question_type, dmq.title AS 'question_title', ddmq.weight AS 'question_weight', dms.standard_deviation AS 'question_standard_deviation', dmq.standard_deviation AS 'answer_standard_deviation' -- ddmq.weight AS 'answer_weight' FROM diagnosis_mainsymp_concsymp dmc LEFT JOIN diagnosis_main_symptoms dms ON dmc.main_symp_code = dms.symp_code LEFT JOIN diagnosis_disease_rate ddr ON (dmc.disease_code = ddr.disease_code and ddr.main_symp_code = dmc.main_symp_code) LEFT JOIN diagnosis_concomitant_symptom dcs ON dmc.conc_symp_code = dcs.symp_code LEFT JOIN diagnosis_mainsymp_question dmq ON dmc.main_symp_code = dmq.main_symp_code LEFT JOIN diagnosis_disease_mainsym_question ddmq ON ( dmc.main_symp_code = ddmq.main_sym_code AND dmc.disease_code = ddmq.disease_code AND dmq.question_code = ddmq.question_code ) WHERE dmc.main_symp_code = #{mainSympCode} AND dmc.conc_symp_code IN
#{item}
AND dmq.question_type = 102 AND IFNULL(dmc.operate_type, '') NOT IN ('D','H') AND IFNULL(ddr.operate_type,'') NOT IN ('D','H') AND dmc.rate != 0
